import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import cookie from 'react-cookies';
import axios from 'axios';

const MyPage = () => {

    const [uuid, setUuid] = useState(cookie.load('uuid'));
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [mno, setMno] = useState('');
    const [phone, setPhone] = useState('');
    const [mtype, setMtype] = useState('');
    const [regdate, setRegdate] = useState('');
    const [sstype, setSstype] = useState('');
    const [pcount, setPcount] = useState('');


    const navigate = useNavigate();

    // 날짜 형식을 변환하는 함수
    const formatDate = (dateString) => {
        const date = new Date(dateString);
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');
        // const seconds = String(date.getSeconds()).padStart(2, '0');
        return `${year}-${month}-${day}   ${hours}:${minutes} `; // ${hours}:${minutes}:${seconds}
    };

    // 전화번호 포맷 변환 함수
    const formatPhoneNumber = (phone) => {
        return phone.replace(/^(\d{3})(\d{3,4})(\d{4})$/, '$1-$2-$3');
    };

    // 이메일 마스킹 함수
    const maskEmail = (email) => {
        if (!email || !email.includes('@')) {
            return '알 수 없음'; // 기본 메시지
        }
        const [localPart, domainPart] = email.split('@'); // 로컬과 도메인 분리
        const maskedLocalPart = localPart.slice(0, 2) + '*'.repeat(localPart.length - 2);
        const [domainName, domainExt] = domainPart.split('.'); // 도메인 이름과 확장자 분리
        const maskedDomain = domainName.slice(0, 1) + '*'.repeat(domainName.length - 1);
        return `${maskedLocalPart}@${maskedDomain}.${domainExt}`;
    };



    const callMemberInfoApi = async () => {
        try {
            // 1. 쿠키에서 토큰 가져오기
            const token = cookie.load('token');
            if (!token) {
                alert("토큰이 없습니다. 로그인해주세요.");
                return false;
            }

            // 2. token을 서버로 보내고 uuid를 받아오기
            const { data: { uuid } } = await axios.post('/api/member/loginCookie', { token });

            // 3. uuid로 회원 타입 가져오기
            const { data: mtype } = await axios.post('/api/member/searchMtype', { uuid });

            // 4. uuid로 회원 정보 조회
            const { data } = await axios.post('/api/member/read', { uuid });

            // 5. 받아온 데이터로 상태 업데이트
            setUuid(data.uuid); // 회원 아이디
            setName(data.name); // 회원 이름
            setEmail(data.email); // 회원 이메일
            setMtype(data.mtype || mtype); // 회원 타입 (서버에서 mtype 없으면 searchMtype 결과 사용)
            setPhone(data.phone); // 연락처
            setRegdate(formatDate(data.regdate)); // 가입 일자
            setMno(data.mno); // 회원 번호
            setSstype(data.sstype); // 구독 타입
            setPcount(data.pcount); // 잔여 포인트
        } catch (error) {
            console.error("회원 정보를 가져오는 중 오류 발생:", error);
            alert("회원 정보를 불러오는 데 실패했습니다.");
            return false;
        }
    };



    const goToMemberList = () => {
        navigate.push('/MemberList');
    };

    useEffect(() => {
        callMemberInfoApi(); // 컴포넌트 마운트 시 API 호출
    }, []); // 빈 배열을 전달하여 최초 한 번만 실행되도록 설정



    // mtype을 변환하는 함수
    const displayMtype = () => {
        if (mtype === 't') {
            return '전문가';
        } else if (mtype === 'm') {
            return '일반회원';
        } else if (mtype === 'a') {
            return '운영자';
        } else {
            return '알 수 없는 타입'; // 다른 값이 있을 때 대비
        }
    };

    return (
        <div>
            <section className="sub_wrap" >
                <article className="s_cnt re_1 ct1">
                    <div className="li_top">
                        <h2 className="s_tit1">MyPage</h2>
                        <form method="post" name="frm">

                            <div className="re1_wrap">
                                <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                                    {mtype === 'a' && (
                                        <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                                            {mtype === 'a' && (
                                                <Link to="/MemberList" className="bt_ty2 submit_ty1" style={{
                                                    fontSize: '15px', width: '120px', height: '30px', alignItems: 'center', justifyContent: 'center', display: 'flex'
                                                }}>
                                                    회원관리
                                                </Link>
                                            )}
                                        </div>
                                    )}
                                </div>

                                <div
                                    style={{ marginTop: '30px' }}
                                    className="My_info re_cnt ct2">
                                    <h3
                                        style={{ color: 'grey', fontSize: '20px' }}>기본정보</h3>

                                        <div
                                        style={{ textAlign: 'right'}}>
                                            <img
                                                style={{ width: '50px' }}
                                                src={require(`../../img/modify/coin.png`)} alt="포인트" />{pcount}p
                                        </div>

                                    <h2>{name}</h2>
                                    <h4>{uuid}</h4>
                                    <br />
                                    <hr />
                                    <br />

                                    <tr>
                                        <th>
                                            <img
                                                style={{ width: '30px' }}
                                                src={require(`../../img/modify/폰2.png`)} alt="연락처" />
                                        </th>
                                        <td>{formatPhoneNumber(phone)}</td>
                                    </tr>

                                    <tr>
                                        <th><img
                                            style={{ width: '30px' }}
                                            src={require(`../../img/modify/email.png`)} alt="이메일" /></th>
                                        <td>{maskEmail(email)}</td>
                                        <button type="button" className="btn-modi">수정</button>
                                    </tr>


                                </div>

                                <div
                                    className="My_info re_cnt ct2"
                                    style={{ marginTop: '30px' }}
                                >
                                    <h3
                                        style={{ color: 'grey', fontSize: '20px' }}>등록정보</h3>
                                    <br />

                                    <tr>
                                        <th> <img
                                            style={{ width: '30px' }}
                                            src={require(`../../img/modify/가입일자.png`)} alt="달력" />
                                        </th>
                                        <td>{regdate}</td>
                                    </tr>

                                    <tr>
                                        <th>회원타입</th>
                                        <td>{displayMtype()}</td>
                                    </tr>

                                    <tr>
                                        <th>구독타입</th>
                                        <td>{displayMtype()}</td>
                                        <button type="button" className="btn-modi">수정</button>
                                    </tr>

                                </div>









                                <div className="re_cnt ct2">
                                    <table className="table_ty1">

                                        <tr>
                                            <th>회원번호</th>
                                            <td>
                                                <input name="mno" id="mno" readOnly="readonly" value={mno} />
                                            </td>
                                        </tr>
                                        <tr className="re_email">
                                            <th>아이디</th>
                                            <td>
                                                <input name="uuid" id="uuid" readOnly="readonly" value={uuid} />
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>이름</th>
                                            <td>
                                                <input name="name" id="name" readOnly="readonly" value={name} />
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>연락처</th>
                                            <td>
                                                <input id="phone" type="text" name="phone" readOnly="readonly" value={formatPhoneNumber(phone)}
                                                />
                                            </td>
                                        </tr>
                                        <tr className="re_email">
                                            <th>이메일</th>
                                            <td>
                                                <input id="email" type="text" name="email" readOnly="readonly" value={email ? maskEmail(email) : '정보 없음'}
                                                />
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>회원타입</th>
                                            <td>
                                                <input id="mtype" type="text" name="mtype" readOnly="readonly" value={displayMtype()}
                                                />
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>가입일자</th>
                                            <td>
                                                <input id="regdate" type="text" name="regdate" readOnly="readonly" value={regdate}
                                                />
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>구독타입</th>
                                            <td>
                                                <input id="sstype" type="text" name="sstype" readOnly="readonly" value={sstype}
                                                />
                                            </td>
                                            <button className="bt_ty3 bt_ty2 submit_ty1">구독타입 변경하기</button>
                                        </tr>
                                        <tr>
                                            <th>잔여 포인트</th>
                                            <td>
                                                <input id="pcount" type="text" name="pcount" readOnly="readonly" value={pcount}
                                                />
                                            </td>
                                            <button className="bt_ty3 bt_ty2 submit_ty1"  >포인트 충전</button>
                                        </tr>
                                        {/* {appendCarList} */}
                                    </table>
                                </div>
                            </div>









                            <div className="btn_confirm">
                                <Link to={'/Modify'} className="bt_ty bt_ty2 submit_ty1 modifyclass">프로필수정</Link>
                            </div>
                        </form>
                    </div>
                </article >
            </section >
        </div >
    );
}

export default MyPage;
