            const sortedChallenges = fetchedChallenges.sort((a, b) => new Date(b.wdate) - new Date(a.wdate)); // 최신순 정렬
            setChallenges(sortedChallenges);
            setTotalPages(Math.ceil(sortedChallenges.length / itemsPerPage));
